import React from "react";

/* The "RecipeList" 'function/component' */
function RecipeList({ recipes, deleteRecipe }) {
  
  // TODO: Display the list of recipes using the structure of table that is provided.
  // TODO: Create at least one additional component that is used by this component.
  // TODO: Each recipe row must have a delete button - <button name="delete">Delete</button> - that deletes the post when clicked.

  const getIndexToDelete = index => {
    deleteRecipe(index.target.value)
  }


const createRecipeTableData = recipes.map((recipe, index) => <tr key={index}><td><p>{recipe.name}</p></td><td><p>{recipe.cuisine}</p></td><td><img src={recipe.photo} /></td><td><p>{recipe.ingredients}</p></td><td><p>{recipe.preparation}</p></td><td><button name="delete" onClick={getIndexToDelete} value={index} >Delete</button></td> </tr>)

  return (
    <div className="recipe-list">
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Cuisine</th>
            <th>Photo</th>
            <th>Ingredients</th>
            <th>preparation</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
        {createRecipeTableData}
        </tbody>
      </table>
    </div>
  );
}

export default RecipeList;
